import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WeatherApp.css';
import clear from '../Assets/clear.png';
import cloud from '../Assets/cloud.png';
import drizzle from '../Assets/drizzle.png';
import humidity from '../Assets/humidity.png';
import rain from '../Assets/rain.png';
import search from '../Assets/search.png';
import snow from '../Assets/snow.png';
import wind from '../Assets/wind.png';

const WeatherApp = () => {
  const [weatherData, setWeatherData] = useState({});
  const [cityName, setCityName] = useState('');
  const [apiKey] = useState('329492ab4b9cd458b154b3dd1a3c483b');
  const [loading, setLoading] = useState(false);

  const apiUrl = 'https://api.openweathermap.org/data/2.5/weather';

  useEffect(() => {
    if (cityName) {
      fetchWeatherData();
    }
  }, [cityName]);

  const fetchWeatherData = async () => {
    try {
      setLoading(true);
      const response = await axios.get(apiUrl, {
        params: {
          q: cityName,
          appid: apiKey,
          units: 'metric', // You can change units to imperial for Fahrenheit
        },
      });
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="top-bar">
        <input
          type="text"
          className="cityname"
          placeholder="SEARCH"
          value={cityName}
          onChange={(e) => setCityName(e.target.value)}
        />
        <div className="search_icon" onClick={fetchWeatherData}>
          <img src={search} alt="" />
        </div>
        {/* Display weather information */}
        {weatherData.main && (
          <>
            <div className="weather-image">
              <img src={cloud} alt="" />
            </div>
            <div className="weather-temp">{weatherData.main.temp}Â°C</div>
            <div className="weather-location">{weatherData.name}</div>
            <div className="data-container">
              <div className="element">
                <img src={humidity} alt="" className="icon" />
                <div className="data">
                  <div className="humidity-percent">{weatherData.main.humidity}%</div>
                  <div className="text">Humidity</div>
                </div>
              </div>
              <div className="element">
                <img src={wind} alt="" className="icon" />
                <div className="data">
                  <div className="humidity-percent">{weatherData.wind.speed} km/h</div>
                  <div className="text">Wind Speed</div>
                </div>
              </div>
            </div>
          </>
        )}
        {loading && <p>Loading...</p>}
      </div>
    </div>
  );
};

export default WeatherApp;
